            // Remove event listener
    
            if (this.readingGuideMouseMoveHandler) {
    
                document.removeEventListener('mousemove', this.readingGuideMouseMoveHandler);
    
                this.readingGuideMouseMoveHandler = null;
    
            }
    
            
    
